import React from 'react';

const Header = (props) => {
  return (
    <div>
        <h1>{props.course}</h1>
    </div>
  )
}

const Part = (props) => {
  return (
    <p>{props.parts} {props.parts}</p>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part p = {props.parts[0]} ex = {props.parts[0]} />
      <Part p = {props.parts[1]} ex = {props.parts[1]} />
      <Part p = {props.parts[2]} ex = {props.parts[2]} />

    </div>
  )
}



const Total = (props) => {
  return (
    <div>
         <p>Number of exercises {props.ex1 + props.ex2 + props.ex3 } </p>
    </div>
  )
}


const App = () => {
    const course = {
      name: 'Half Stack application development',
      parts: [
        {
      name: 'Fundamentals of React',
      exercises: 10
    },
   {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]
    } 

  return (
  <div>
    <Header course = {course.name} />
    <Content parts = {course.parts} />
    <Total parts = {course.parts}  />
  </div>
)
  }

export default App;
