import React from 'react';

const Header = (props) => {
  return (
    <div>
        <h1>{props.course}</h1>
    </div>
  )
}

const Part = (props) => {
  return (
    <p>{props.p} {props.ex}</p>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part p = {props.p1} ex = {props.ex1} />
      <Part p = {props.p2} ex = {props.ex2} />
      <Part p = {props.p3} ex = {props.ex3} />

    </div>
  )
}



const Total = (props) => {
  return (
    <div>
         <p>Number of exercises {props.ex1 + props.ex2 + props.ex3 } </p>

    </div>
  )
}


const App = () => {
    const course = 'Half Stack application development'
    const part1 = {
      name: 'Fundamentals of React',
      exercises: 10
    }
    const part2 = {
      name: 'Using props to pass data',
      exercises: 7
    }
    const part3 = {
      name: 'State of a component',
      exercises: 14
    }

  return (
  <div>
    <Header course = {course} />
    <Content p1={part1.name} p2={part2.name} p3={part3.name} ex1 = {part1.exercises} ex2 = {part2.exercises} ex3 = {part3.exercises}   />
    <Total ex1 = {exercises1} ex2 = {exercises2} ex3 = {exercises3}  />
  </div>
)
  }

export default App;
